public class DivBy3and5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("\n");
		
		int i;
		
		for (i = 1 + 1; i <= 100 - 1; i++)
		{
			if (i % 3 == 0 && i % 5 == 0)
			{
				System.out.print(i + "\n");
			}
		}
		
		System.out.print("\n");
	
		for (i = 1 + 1; i <= 100 - 1; i++)
			{
				if (i % 3 == 0)
				{
					System.out.print(i + "\n");
				}
			}
		System.out.print("\n");
		for (i = 1 + 1; i <= 100 - 1; i++)
				{
					if (i % 5 == 0)
					{
						System.out.print(i + "\n");
					}
				}
				}
			}
		
		
	


